package rw.bk.filmfan.activities.movie_details.adapter;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import androidx.recyclerview.widget.RecyclerView;import java.util.List;import rw.bk.filmfan.R;import rw.bk.filmfan.models.genre;public class genre_adapter extends RecyclerView.Adapter<genre_adapter.ViewHolder> {    List<genre> genres;    public genre_adapter(List<genre> movies, Context context){        super();        this.genres = movies;    }    @Override    public genre_adapter.ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View v = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_genre, parent, false);        genre_adapter.ViewHolder viewHolder = new genre_adapter.ViewHolder(v);        return viewHolder;    }    @Override    public void onBindViewHolder(final genre_adapter.ViewHolder Viewholder, final int position) {        final genre genre_data =  genres.get(position);        Viewholder.name.setText(genre_data.getName());    }    @Override    public int getItemCount() {        return genres.size();    }    class ViewHolder extends RecyclerView.ViewHolder{        public TextView name;        public ViewHolder(View itemView) {            super(itemView);            name= itemView.findViewById(R.id.genre_name);        }    }}