package rw.bk.filmfan.helpers;import java.text.SimpleDateFormat;import java.util.Date;public class dateHelpers {    public static String convertdate(String release_year){        Date r_year=null;        try{            r_year=new SimpleDateFormat("yyyy").parse(release_year);        }catch (Exception e){            e.printStackTrace();        }      return r_year.toString();    }}